document.addEventListener("DOMContentLoaded",()=>{$loadUsers("div.room"),$roomControl("form.room-kick"),$roomControl("form.user-kick")});const $loadUsers=e=>{const t=document.querySelectorAll(e);t.forEach(e=>{const t=e.dataset.room,n=`#room-${t}-users`,s=`user.list.php?room=${t}`;setInterval(async function(){const e=await fetch(s,{cache:"no-cache",headers:{"Cache-Control":"no-cache"}});document.querySelector(n).innerHTML=await e.text()},1e3)})},$roomControl=e=>{const t=document.querySelectorAll(e);t.forEach(e=>{e.addEventListener("submit",function(e){const t=this,n=t.getAttribute("action"),s=t.getAttribute("method"),o=new FormData(t),i=t.querySelector('button[type="submit"]');fetch(n,{method:s,body:o}).then(e=>e.text()),$lockUnlock(i),e.preventDefault()})})},$lockUnlock=e=>{const t=e.querySelector("i"),n=t.className;e.disabled=!0,t.className="fas fa-cog fa-spin fa-fw",setTimeout(function(){e.disabled=!1,t.className=n},5e3)}